-- Loop through and drop MS_Description from each column of GLTRANS
DECLARE @ColumnName NVARCHAR(128);
DECLARE @Sql NVARCHAR(MAX);

DECLARE column_cursor CURSOR FOR
SELECT c.name
FROM sys.columns c
JOIN sys.tables t ON c.object_id = t.object_id
WHERE t.name = 'GLTRANS';

OPEN column_cursor;
FETCH NEXT FROM column_cursor INTO @ColumnName;

WHILE @@FETCH_STATUS = 0
BEGIN
    BEGIN TRY
        SET @Sql = '
        EXEC sp_dropextendedproperty 
            @name = N''MS_Description'', 
            @level0type = N''SCHEMA'', @level0name = N''dbo'',
            @level1type = N''TABLE'',  @level1name = N''GLTRANS'',
            @level2type = N''COLUMN'', @level2name = @Col;
        ';
        EXEC sp_executesql @Sql, N'@Col NVARCHAR(128)', @Col = @ColumnName;
        PRINT ' Removed description from GLTRANS.' + @ColumnName;
    END TRY
    BEGIN CATCH
        PRINT ' Skipped: GLTRANS.' + @ColumnName + '  ' + ERROR_MESSAGE();
    END CATCH;

    FETCH NEXT FROM column_cursor INTO @ColumnName;
END

CLOSE column_cursor;
