
CREATE PROCEDURE [mrs].[mysp_QuerySelector]
    @QueryID varchar(20),              -- Identifies which query to run
    @StartDate INT = 20250101; 
    @EndDate   INT = 20250228;
    @DebugMode BIT = 0         -- As per your guidelines
    
    DECLARE @MinItem =  '50000' = ,    --PO BDITEM, USAGE IHITEM
    DECLARE @MaxItem  = '99998'    
    DECLARE @MinItem2 = '49999',    --ROP IMITEM
    DECLARE @MaxItem2 = '90000'

AS
BEGIN
    SET NOCOUNT ON;
    
    DECLARE @SqlQuery NVARCHAR(MAX);
    
    -- Select query based on parameter
    CASE @QueryID
        WHEN 'help' THEN            
            SET @SqlQuery = 'SELECT [ROP_PRICE], [ROP_MP], [ROP_PO], [ROP_ROP]
                            [SALES_DATA], [TAG], [USAGE], [USAGE_SUM], 
                            [CM], [CUST_SUM], [CUST_SUM1], [ITEMONHD],
                            [SALES_ORDERS], [SPO], [SHIPMAST], [GLTRANS],
                            [SHIPMENTS]'
                
        WHEN 'ROP_PRICE
            SET @SqlQuery = 'SELECT 'price', [mrs].[z_Item_on_Hand_File_____ITEMONHD].[ITEM_NUMBER_____IOITEM], [z_Item_on_Hand_File_____ITEMONHD].[Base_Price_____IOBPRC] FROM [mrs].[z_Item_on_Hand_File_____ITEMONHD];'

        WHEN ROP_MP'
            SET @SqlQuery = 'SELECT 'Material processing query', det.[Transaction_#_____MDORDR], vend.[Vendor_#_____VVNDR], vend.[Vendor_Alpha_Search_____VALPHA], det.[=_OPEN__C_=_CLOSED_____MDCLOS], det.[ITEM_NUMBER_____MDITEM], det.[CRT_DESCRIPTION_____MDCRTD], (CAST(CAST(det.[REQUESTED_SHIP_CENTURY_____MDRQCC] AS DECIMAL(5,1)) AS INT)*1000000+CAST(CAST(det.[REQUESTED_SHIP_YEAR_____MDRQYY] AS DECIMAL(5,1)) AS INT)*10000+CAST(CAST(det.[REQUESTED_SHIP_MONTH_____MDRQMM] AS DECIMAL(5,1)) AS INT)*100+CAST(CAST(det.[REQUESTED_SHIP_DAY_____MDRQDD] AS DECIMAL(5,1)) AS INT)) AS CalculatedShipDate, det.[ON_ORDER_QUANTITY_____MDOQTY], det.[ORIGINAL_ORDERED_UOM_____MDOUOM], det.[ORDER_QUANTITY_____MDIQTY] FROM [mrs].[z_Vendor_Master_File_____APVEND] vend INNER JOIN [mrs].[z_Material_Processing_Order_Detail_____MPDETAIL] det ON vend.[COMPANY_NUMBER_____VCOMP] = det.[COMPANY_NUMBER_____MDCOMP] AND vend.[Vendor_#_____VVNDR] = det.[Vendor_#_____MDVNDR] ORDER BY det.[ITEM_NUMBER_____MDITEM], det.[Transaction_#_____MDORDR] DESC;'

        WHEN 'ROP_PO'
            --Parameters: Min Max ITEM
            SET @SqlQuery = 'SELECT 'Purchase order query', pod.[Transaction_#_____BDPONO], pod.[Vendor_#_____BDVNDR], vend.[Vendor_Name_____VNAME], pod.[Record_Code_____BDRECD], pod.[ITEM_NUMBER_____BDITEM], pod.[CRT_Description_____BDCRTD], (CAST(CAST(pod.[Revised_Promise_Century_____BDRPCC] AS DECIMAL(5,1)) AS INT)*1000000+CAST(CAST(pod.[Revised_Promise_Year_____BDRPYY] AS DECIMAL(5,1)) AS INT)*10000+CAST(CAST(pod.[Revised_Promise_Month_____BDRPMM] AS DECIMAL(5,1)) AS INT)*100+CAST(CAST(pod.[Revised_Promise_Day_____BDRPDD] AS DECIMAL(5,1)) AS INT)) AS RevisedPromiseDate, pod.[Original_Inventory_Quantity_____BDOQOO], pod.[Inventory_UOM_____BDIUOM], pod.[Current_Inv._Quantity_Ordered_/_Received_____BDCQOO], pod.[Original_Ordered__Quantity_____BDOOQ], pod.[Total_Inventory_Quantity_Received_____BDTIQR], (CAST(CAST(pod.[Purchase_Order_Century_____BDPOCC] AS DECIMAL(5,1)) AS INT)*1000000+CAST(CAST(pod.[Purchase_Order_Year_____BDPOYY] AS DECIMAL(5,1)) AS INT)*10000+CAST(CAST(pod.[Purchase_Order_Month_____BDPOMM] AS DECIMAL(5,1)) AS INT)*100+CAST(CAST(pod.[Purchase_Order_Day_____BDPODD] AS DECIMAL(5,1)) AS INT)) AS PurchaseOrderDate, (CAST(CAST(pod.[Received_Century_____BDRCCC] AS DECIMAL(5,1)) AS INT)*1000000+CAST(CAST(pod.[Received_Year_____BDRCYY] AS DECIMAL(5,1)) AS INT)*10000+CAST(CAST(pod.[Received_Month_____BDRCMM] AS DECIMAL(5,1)) AS INT)*100+CAST(CAST(pod.[Received_Day_____BDRCDD] AS DECIMAL(5,1)) AS INT)) AS ReceivedDate FROM [mrs].[z_Purchase_Order_Detail_File_____PODETAIL] pod LEFT OUTER JOIN [mrs].[z_Vendor_Master_File_____APVEND] vend ON pod.[Vendor_#_____BDVNDR] = vend.[Vendor_#_____VVNDR] WHERE (pod.[ITEM_NUMBER_____BDITEM] Between @MinItem And @MaxItem) ORDER BY pod.[ITEM_NUMBER_____BDITEM], pod.[Transaction_#_____BDPONO] DESC;

        WHEN 'ROP_ROP'
            --Parameters MinItem2, MaxItem2
            SET @SqlQuery = 'SELECT 'ROP Query', [z_Item_Master_File_____ITEMMAST].[Fast_Path_Item_Description_____IMFSP2], [z_Item_Master_File_____ITEMMAST].[ITEM_NUMBER_____IMITEM], [z_Item_Master_File_____ITEMMAST].[Size_1_____IMSIZ1], [z_Item_Master_File_____ITEMMAST].[Size_2_____IMSIZ2], [z_Item_Master_File_____ITEMMAST].[Size_3_____IMSIZ3], [z_Item_Master_File_____ITEMMAST].[Item_Print_Descr_Two_____IMDSC2], [z_Item_Master_File_____ITEMMAST].[Weight_Per_Foot_____IMWPFT], [z_Item_on_Hand_File_____ITEMONHD].[Average_Cost_____IOACST], [z_Item_on_Hand_File_____ITEMONHD].[Qty_On_Hand_____IOQOH], [z_Item_on_Hand_File_____ITEMONHD].[Qty_On_Order_____IOQOO], [z_Item_on_Hand_File_____ITEMONHD].[Qty_On_Reserved_____IOQOR]+[z_Item_on_Hand_File_____ITEMONHD].[Qty_On_Order_Rsrvd_____IOQOOR], [z_Item_Master_File_____ITEMMAST].[Item_Print_Descr_One_____IMDSC1], [z_Item_Master_File_____ITEMMAST].[PSI_=_PER_SQUARE_INCH_PLI_=_PER_LINEAR_INCH_"___"=_____IMWUOM], [z_Item_Master_File_____ITEMMAST].[Company_SMO_____IMCSMO] FROM [mrs].[z_Item_Master_File_____ITEMMAST] [z_Item_Master_File_____ITEMMAST], [mrs].[z_Item_on_Hand_File_____ITEMONHD] [z_Item_on_Hand_File_____ITEMONHD] WHERE [z_Item_on_Hand_File_____ITEMONHD].[ITEM_NUMBER_____IOITEM] = [z_Item_Master_File_____ITEMMAST].[ITEM_NUMBER_____IMITEM] AND (([z_Item_Master_File_____ITEMMAST].[Record_Code_____IMRECD]='A') AND ([z_Item_Master_File_____ITEMMAST].[ITEM_NUMBER_____IMITEM] Between @MinItem2 And @MaxItem2)) ORDER BY [z_Item_Master_File_____ITEMMAST].[Fast_Path_Item_Description_____IMFSP2];'

        WHEN 'SALES_DATA'
            SET @SqlQuery = 'SELECT 'ROP - Sales data Query', O.[Record_Code_____OORECD], D.[Order_Type_____ODTYPE], D.[ITEM_NUMBER_____ODITEM], D.[Transaction_#_____ODORDR], O.[CUSTOMER_NUMBER_____OOCUST], C.[Customer_Alpha_Name_____CALPHA], D.[TOTAL_LBS_____ODTLBS], D.[TOTAL_FTS_____ODTFTS], CAST(CAST(O.[ORDER_DATE_CENTURY_____OOOCC] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(O.[ORDER_DATE_YEAR_____OOOYY] AS DECIMAL(5,1)) AS INT)*10000 + CAST(CAST(O.[ORDER_DATE_MONTH_____OOOMM] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(O.[ORDER_DATE_DAY_____OOODD] AS DECIMAL(5,1)) AS INT), CAST(CAST(C.[Salesman_One_District_Number_____CSMDI1] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(C.[Salesman_One_Number_____CSLMN1] AS DECIMAL(5,1)) AS INT) FROM [mrs].[z_Customer_Master_File_____ARCUST] C JOIN [mrs].[z_Open_Order_File_____OEOPNORD] O ON O.[CUSTOMER_NUMBER_____OOCUST] = C.[CUSTOMER_NUMBER_____CCUST] JOIN [mrs].[z_Order_Detail_File_____OEDETAIL] D ON D.[Transaction_#_____ODORDR] = O.[ORDER_NUMBER_____OOORDR] WHERE O.[Record_Code_____OORECD] = 'A' AND D.[Order_Type_____ODTYPE] IN ('A', 'C') ORDER BY D.[ITEM_NUMBER_____ODITEM];'

        WHEN 'TAG'
            --Params: MinItem2
            SET @SqlQuery = 'SELECT 'ROP - TAG query', [z_Tag_Master_File_____ITEMTAG].[ITEM_NUMBER_____ITITEM], [z_Tag_Master_File_____ITEMTAG].[TAG_NUMBER_ALPHA_____ITTAG], [z_Tag_Master_File_____ITEMTAG].[Tag_Master_Description_____ITTDES], [z_Tag_Master_File_____ITEMTAG].[Numeric_Length_____ITLNTH], [z_Tag_Master_File_____ITEMTAG].[Tag_Pieces_____ITTPCS], [z_Tag_Master_File_____ITEMTAG].[Heat_#_____ITHEAT], [z_Tag_Master_File_____ITEMTAG].[Mill_Coil#_____ITV301], [z_Tag_Master_File_____ITEMTAG].[Warehouse_Loc_____ITLOCT], [z_Tag_Master_File_____ITEMTAG].[Tag_Quantity_____ITTQTY], [z_Tag_Master_File_____ITEMTAG].[Reserved_Quantity_____ITRQTY], [z_Item_Master_File_____ITEMMAST].[Fast_Path_Item_Description_____IMFSP2] FROM [mrs].[z_Item_Master_File_____ITEMMAST] [z_Item_Master_File_____ITEMMAST], [mrs].[z_Tag_Master_File_____ITEMTAG] [z_Tag_Master_File_____ITEMTAG] WHERE [z_Tag_Master_File_____ITEMTAG].[ITEM_NUMBER_____ITITEM] = [z_Item_Master_File_____ITEMMAST].[ITEM_NUMBER_____IMITEM] AND ([z_Tag_Master_File_____ITEMTAG].[Record_Code_____ITRECD] = 'A' AND [z_Tag_Master_File_____ITEMTAG].[ITEM_NUMBER_____ITITEM] > @MinItem2) ORDER BY [z_Tag_Master_File_____ITEMTAG].[ITEM_NUMBER_____ITITEM], [z_Tag_Master_File_____ITEMTAG].[Heat_#_____ITHEAT], [z_Tag_Master_File_____ITEMTAG].[Mill_Coil#_____ITV301], [z_Tag_Master_File_____ITEMTAG].[Numeric_Length_____ITLNTH];'

        WHEN 'USAGE'
            -- Parameters MinItem, MaxItem
            SET @SqlQuery = 'SELECT 'ROP - Usage query', T.[ITEM_NUMBER_____IHITEM], T.[Transaction_Type_____IHTRNT], T.[Transaction_#_____IHTRN#], T.[Vendor_#_____IHVNDR], T.[CUSTOMER_NUMBER_____IHCUST], T.[Trans_YY_____IHTRYY], T.[Trans_MM_____IHTRMM], T.[Trans_DD_____IHTRDD], T.[Trans_Inv_Qty_____IHTQTY] FROM [mrs].[z_Item_Transaction_History_____ITEMHIST] T WHERE (CAST(CAST(T.[Trans_CC_____IHTRCC] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(T.[Trans_YY_____IHTRYY] AS DECIMAL(5,1)) AS INT)*10000 + CAST(CAST(T.[Trans_MM_____IHTRMM] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(T.[Trans_DD_____IHTRDD] AS DECIMAL(5,1)) AS INT) > @StartDate) AND (T.[ITEM_NUMBER_____IHITEM] BETWEEN @MinItem AND @MaxItem) ORDER BY T.[ITEM_NUMBER_____IHITEM], T.[Trans_YY_____IHTRYY], T.[Trans_MM_____IHTRMM], T.[Trans_DD_____IHTRDD];'
            

        WHEN 'USAGE_SUM'
            --Parameters
            SET @SqlQuery = 'SELECT DISTINCT 'ROP - Usage sum query', T.[ITEM_NUMBER_____IHITEM], T.[Transaction_Type_____IHTRNT], T.[CUSTOMER_NUMBER_____IHCUST], C.[Customer_Alpha_Name_____CALPHA], CAST(CAST(C.[Salesman_One_District_Number_____CSMDI1] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(C.[Salesman_One_Number_____CSLMN1] AS DECIMAL(5,1)) AS INT) FROM [mrs].[z_Customer_Master_File_____ARCUST] C INNER JOIN [mrs].[z_Item_Transaction_History_____ITEMHIST] T ON C.[CUSTOMER_NUMBER_____CCUST] = T.[CUSTOMER_NUMBER_____IHCUST] WHERE (CAST(CAST(T.[Trans_CC_____IHTRCC] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(T.[Trans_YY_____IHTRYY] AS DECIMAL(5,1)) AS INT)*10000 + CAST(CAST(T.[Trans_MM_____IHTRMM] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(T.[Trans_DD_____IHTRDD] AS DECIMAL(5,1)) AS INT) > @StartDate) AND (T.[ITEM_NUMBER_____IHITEM] BETWEEN @MinItem AND @MaxItem) AND (T.[Transaction_Type_____IHTRNT] IN ('CR', 'IN')) ORDER BY T.[ITEM_NUMBER_____IHITEM], C.[Customer_Alpha_Name_____CALPHA];'

        WHEN 'CM'
            -- Parameters. C = Credit. W = ??. start date, end date
            -- Create codes OEDistrict_ODDIST   +Transation_ORORDR  
            --              CusDistrict_ODCDIS  +Customer_ODCUST 

            --DECLARE @StartDate INT = 20250101; DECLARE @EndDate   INT = 20250228;
            -- StartDate EndDate
            SET @SqlQuery = 'SELECT 'Sales Analisis - credit memos', CAST(CAST(D.[O/E_District_#_____ODDIST] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(D.[Transaction_#_____ODORDR] AS DECIMAL(10,1)) AS INT) as Order__District_ODDIST_PlusTransaction_ODORDR, S.[Salesman_Name_____SMNAME], O.[Order_Type_____OOTYPE], CAST(CAST(D.[CUSTOMER_DISTRICT_____ODCDIS] AS DECIMAL(5,1)) AS INT)*100000 + CAST(CAST(D.[CUSTOMER_NUMBER_____ODCUST] AS DECIMAL(10,1)) AS INT) as CustomerDistrict_ODCDIS_PlusNumber_ODCUST, C.[Customer_Alpha_Name_____CALPHA], D.[ITEM_NUMBER_____ODITEM], D.[SIZE_1_____ODSIZ1], D.[SIZE_2_____ODSIZ2], D.[SIZE_3_____ODSIZ3], D.[CRT_DESCRIPTION_____ODCRTD], D.[TOTAL_FTS_____ODTFTS], D.[TOTAL_LBS_____ODTLBS], D.[TOTAL_PCS_____ODTPCS], D.[NEGOTIATED_SELL_$_EXT_____ODSLSX], D.[PRORATED_FREIGHT_SALES_____ODFRTS], D.[COSTING_METHOD_COST_EXT_____ODCSTX], D.[TOTAL_PROCESS_COST_____ODPRCC], D.[TOTAL_ADDTL_CHG_COST_____ODADCC], D.[PRORATED_WHOLE_ORDER_COST_AMT_____ODWCCS], C.[State_____CSTAT], C.[Country_____CCTRY], CAST(CAST(O.[INVOICE_CENTURY_____OOICC] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(O.[INVOICE_YEAR_____OOIYY] AS DECIMAL(5,1)) AS INT)*10000 + CAST(CAST(O.[INVOICE_MONTH_____OOIMM] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(O.[INVOICE_DAY_____OOIDD] AS DECIMAL(5,1)) AS INT) as CustInvoiceDate_OOIC, D.[CUSTOMER_REFERENCE_____ODCREF] FROM [mrs].[z_Order_Detail_File_____OEDETAIL] AS D INNER JOIN [mrs].[z_Open_Order_File_____OEOPNORD] AS O ON D.[O/E_District_#_____ODDIST] = O.[O/E_Dist#_____OODIST] AND D.[Transaction_#_____ODORDR] = O.[ORDER_NUMBER_____OOORDR] INNER JOIN [mrs].[z_Customer_Master_File_____ARCUST] AS C ON O.[CUSTOMER_DISTRICT_____OOCDIS] = C.[CUSTOMER_DISTRICT_NUMBER_____CDIST] AND O.[CUSTOMER_NUMBER_____OOCUST] = C.[CUSTOMER_NUMBER_____CCUST] INNER JOIN [mrs].[z_Salesman_Master_File_____SALESMAN] AS S ON O.[INSIDE_SALESMAN_DISTRICT_____OOISMD] = S.[Salesman_District_Number_____SMDIST] AND O.[INSIDE_SALESMAN_NUMBER_____OOISMN] = S.[Salesman_Number_____SMSMAN] WHERE O.[Order_Type_____OOTYPE] = 'C' AND O.[Record_Code_____OORECD] = 'W' AND (CAST(CAST(O.[INVOICE_CENTURY_____OOICC] AS DECIMAL(5,1)) AS INT)*1000000 + CAST(CAST(O.[INVOICE_YEAR_____OOIYY] AS DECIMAL(5,1)) AS INT)*10000 + CAST(CAST(O.[INVOICE_MONTH_____OOIMM] AS DECIMAL(5,1)) AS INT)*100 + CAST(CAST(O.[INVOICE_DAY_____OOIDD] AS DECIMAL(5,1)) AS INT) BETWEEN @StartDate AND @EndDate);'

        WHEN 'CUST_SUM'
            SET @SqlQuery = 'SELECT DISTINCT 'Sales Analysis - customer summary', CAST([z_Customer_Master_File_____ARCUST].[CUSTOMER_DISTRICT_NUMBER_____CDIST] AS INT) * 100000 + CAST([z_Customer_Master_File_____ARCUST].[CUSTOMER_NUMBER_____CCUST] AS INT) as CDIST_CCUST, [z_Customer_Master_File_____ARCUST].[Customer_Alpha_Name_____CALPHA], [z_Customer_Master_File_____ARCUST].[Credit_Limit_____CLIMIT], CAST([z_Customer_Master_File_____ARCUST].[Inside_Salesman_District_Number_____CISMD1] AS INT) * 100 + CAST([z_Customer_Master_File_____ARCUST].[Inside_Salesman_Number_____CISLM1] AS INT) AS Customer_InsideSalesMan_CISMD1_CISLM1, CAST([z_Customer_Master_File_____ARCUST].[Salesman_One_District_Number_____CSMDI1] AS INT) * 100 + CAST([z_Customer_Master_File_____ARCUST].[Salesman_One_Number_____CSLMN1] AS INT) as Customer_Salesman_CSMDI1_CSLMN1, [z_Salesman_Master_File_____SALESMAN].[Salesman_Name_____SMNAME] FROM [mrs].[z_Customer_Master_File_____ARCUST] [z_Customer_Master_File_____ARCUST], [mrs].[z_Salesman_Master_File_____SALESMAN] [z_Salesman_Master_File_____SALESMAN] WHERE [z_Customer_Master_File_____ARCUST].[Inside_Salesman_District_Number_____CISMD1] = [z_Salesman_Master_File_____SALESMAN].[Salesman_District_Number_____SMDIST] AND [z_Customer_Master_File_____ARCUST].[Inside_Salesman_Number_____CISLM1] = [z_Salesman_Master_File_____SALESMAN].[Salesman_Number_____SMSMAN] ORDER BY [z_Customer_Master_File_____ARCUST].[Customer_Alpha_Name_____CALPHA];'

        WHEN 'CUST_SUM1'
            SET @SqlQuery = 'SELECT DISTINCT 'Sales analysis Salesman summary', CAST(CAST([z_Salesman_Master_File_____SALESMAN].[Salesman_District_Number_____SMDIST] AS DECIMAL(18, 2)) AS INT) * 100 + CAST(CAST([z_Salesman_Master_File_____SALESMAN].[Salesman_Number_____SMSMAN] AS DECIMAL(18, 2)) AS INT) AS Salesman_Combined_ID_SALESMAN_SMSMAN, [z_Salesman_Master_File_____SALESMAN].[Salesman_Name_____SMNAME] FROM [mrs].[z_Salesman_Master_File_____SALESMAN] [z_Salesman_Master_File_____SALESMAN];'

        WHEN 'ITEMONHD'
            SET @SqlQuery = ''

        WHEN 'SALES_ORDERS'
            -- Parameters
            SET @SqlQuery = 'DECLARE @StartDate INT = 202501; DECLARE @EndDate INT = 202502; SELECT CAST(CAST([z_Order_Detail_File_____OEDETAIL].[O/E_District_#_____ODDIST] AS DECIMAL(18, 2)) AS INT) * 1000000 + CAST(CAST([z_Order_Detail_File_____OEDETAIL].[Transaction_#_____ODORDR] AS DECIMAL(18, 2)) AS INT) Order_districtPlusTransaction, [z_Salesman_Master_File_____SALESMAN].[Salesman_Name_____SMNAME], [z_Open_Order_File_____OEOPNORD].[Order_Type_____OOTYPE], CAST(CAST([z_Order_Detail_File_____OEDETAIL].[CUSTOMER_DISTRICT_____ODCDIS] AS DECIMAL(18, 2)) AS INT) * 100000 + CAST(CAST([z_Order_Detail_File_____OEDETAIL].[CUSTOMER_NUMBER_____ODCUST] AS DECIMAL(18, 2)) AS INT) Customer_DistrictPlusCustomer, [z_Customer_Master_File_____ARCUST].[Customer_Alpha_Name_____CALPHA], CAST(CAST([z_Open_Order_File_____OEOPNORD].[INVOICE_CENTURY_____OOICC] AS DECIMAL(18, 2)) AS INT) * 1000000 + CAST(CAST([z_Open_Order_File_____OEOPNORD].[INVOICE_YEAR_____OOIYY] AS DECIMAL(18, 2)) AS INT) * 10000 + CAST(CAST([z_Open_Order_File_____OEOPNORD].[INVOICE_MONTH_____OOIMM] AS DECIMAL(18, 2)) AS INT) * 100 + CAST(CAST([z_Open_Order_File_____OEOPNORD].[INVOICE_DAY_____OOIDD] AS DECIMAL(18, 2)) AS INT) as InvoiceDate_OOI, [z_Order_Detail_File_____OEDETAIL].[ITEM_NUMBER_____ODITEM], [z_Order_Detail_File_____OEDETAIL].[SIZE_1_____ODSIZ1], [z_Order_Detail_File_____OEDETAIL].[SIZE_2_____ODSIZ2], [z_Order_Detail_File_____OEDETAIL].[SIZE_3_____ODSIZ3], [z_Order_Detail_File_____OEDETAIL].[CRT_DESCRIPTION_____ODCRTD], [z_Sales_Description_Override_____SLSDSCOV].[Item_Print_Descr_Two_____DXDSC2], [z_Order_Detail_File_____OEDETAIL].[TOTAL_FTS_____ODTFTS], [z_Order_Detail_File_____OEDETAIL].[TOTAL_LBS_____ODTLBS], [z_Order_Detail_File_____OEDETAIL].[TOTAL_PCS_____ODTPCS], [z_Order_Detail_File_____OEDETAIL].[NEGOTIATED_SELL_$_EXT_____ODSLSX], [z_Order_Detail_File_____OEDETAIL].[PRORATED_FREIGHT_SALES_____ODFRTS], [z_Order_Detail_File_____OEDETAIL].[COSTING_METHOD_COST_EXT_____ODCSTX], [z_Order_Detail_File_____OEDETAIL].[TOTAL_PROCESS_COST_____ODPRCC], [z_Order_Detail_File_____OEDETAIL].[TOTAL_ADDTL_CHG_COST_____ODADCC], [z_Order_Detail_File_____OEDETAIL].[PRORATED_WHOLE_ORDER_COST_AMT_____ODWCCS], [z_Customer_Master_File_____ARCUST].[State_____CSTAT], [z_Customer_Master_File_____ARCUST].[Country_____CCTRY] FROM [mrs].[z_Customer_Master_File_____ARCUST] AS [z_Customer_Master_File_____ARCUST], [mrs].[z_Order_Detail_File_____OEDETAIL] AS [z_Order_Detail_File_____OEDETAIL], [mrs].[z_Open_Order_File_____OEOPNORD] AS [z_Open_Order_File_____OEOPNORD], [mrs].[z_Salesman_Master_File_____SALESMAN] AS [z_Salesman_Master_File_____SALESMAN], [mrs].[z_Sales_Description_Override_____SLSDSCOV] AS [z_Sales_Description_Override_____SLSDSCOV] WHERE CAST(CAST([z_Order_Detail_File_____OEDETAIL].[O/E_District_#_____ODDIST] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Open_Order_File_____OEOPNORD].[O/E_Dist#_____OODIST] AS DECIMAL(18, 2)) AS INT) AND CAST(CAST([z_Order_Detail_File_____OEDETAIL].[O/E_District_#_____ODDIST] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Sales_Description_Override_____SLSDSCOV].[Tran._District_#_____DXDIST] AS DECIMAL(18, 2)) AS INT) AND CAST(CAST([z_Order_Detail_File_____OEDETAIL].[MAIN_LINE_____ODMLIN] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Sales_Description_Override_____SLSDSCOV].[MAIN_LINE_____DXMLIN] AS DECIMAL(18, 2)) AS INT) AND CAST(CAST([z_Order_Detail_File_____OEDETAIL].[Transaction_#_____ODORDR] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Open_Order_File_____OEOPNORD].[ORDER_NUMBER_____OOORDR] AS DECIMAL(18, 2)) AS INT) AND CAST(CAST([z_Order_Detail_File_____OEDETAIL].[Transaction_#_____ODORDR] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Sales_Description_Override_____SLSDSCOV].[Transaction_#_____DXORDR] AS DECIMAL(18, 2)) AS INT) AND CAST(CAST([z_Open_Order_File_____OEOPNORD].[CUSTOMER_DISTRICT_____OOCDIS] AS DECIMAL(18, 2)) AS INT) = CAST(CAST([z_Customer_Master_File_____ARCUST].[CUSTOMER_DISTRICT_NUMBER_____CDIST] AS DECIMAL(18, 2)) AS INT) AND CAST(CA